import {legacy_createStore, applyMiddlewere} from 'redux';
import {composeWithDevTools} from 'redux-devtools-extension';
import thunk from 'redux-thunk';
import rootReducer from '../reducer';

export const store = legacy_createStore(rootReducer, composeWithDevTools(applyMiddlewere(thunk)))
