import React from "react";
import {useState, useEffect} from 'react';
import {useDispatch, useSelector} from 'react-redux';
import { getCharacters } from "../action";
import {Link} from 'react-router-dom'
import Card from "./Card";
import Paginado from "./Paginado";

export default function Home(){
    const dispatch = useDispatch()
    const allCharacters = useSelector((state) => state.characters)
    const [currentPage, setCurrentPage] = useState(1)
    const [charactersPerPage, setCharactersPerPage] = useState(6)
    const indexOfLastCharacter = currentPage * charactersPerPage //6
    const indexOfFirstCharacter = indexOfLastCharacter - charactersPerPage //0
    const currentCharacters = allCharacters.slice(indexOfFirstCharacter, indexOfLastCharacter)

    const paginado = (pageNumber) => {
        setCurrentPage(pageNumber)
    }

    useEffect(() => {
        dispatch(getCharacters())
    },[dispatch])
    function handleClick(e){
     e.preventDefault();
     dispatch(getCharacters());
    }

    return(
        <div>
            <Link to= '/characters'>Crear personaje</Link>
            <h1>Games of Thrones</h1>
            <button onClick={e => {handleClick(e)}}>
                volver a cargar todos los personajes
            </button>
            <div>
                <select>
                    <option value= 'asc'>Ascendente</option>
                    <option value= 'desc'>Descendente</option>
                </select>
                <select>
                    <option value = 'All'>Todos</option>
                    <option value = 'Alive'>vivo</option>
                    <option value = 'Deceased'>muerto</option>
                    <option velue = 'Unknow'>descocido</option>
                    <option value = 'Presumed dead'>probablemente muerto</option>
                 </select>
                 <select>
                    <option value = 'All'>Todos</option>
                    <option value = 'created'>creados</option>
                    <option value = 'api'>Existente</option>
                 </select>

                 <Paginado 
                 charactersPerPage ={charactersPerPage}
                 allCharacters = {allCharacters.length}
                 paginado = {paginado}
                 />
             {
              currentCharacters?.map((el) =>{
                return(
                  <>
                 <Link key= {el.id} to = {'/home/'+ el.id}>
                    <Card    
                    fullname={el.fullname}
                    title={el.title} 
                    family={el.family} 
                    imageUrl={el.imageUrl}
                   />
                 </Link>
                 </>
                );
                })
             }    
            </div>
        </div>
    )
}
